import React, { useState } from "react";

// import link
import { NavLink, Link } from "react-router-dom";
// import images
import intLOGO from "../../assets/images/interactlogo.SVG";
// import components

const navLink = [
  {
    path: "/homepage",
    display: "Home",
  },
  {
    path: "/about",
    display: "About",
  },
  {
    path: "/checkin",
    display: "Activities",
  },
  {
    path: "/contact",
    display: "Contact",
  },
];

const Header = () => {
  const [toggle, setToggle] = useState(false);
  const handleClick = () => setToggle(!toggle);

  return (
    <>
      <div className="w-full h-[80px] border-b">
        <div className="md:max-w-[1080px] sm:max-w-[600px] m-auto w-full h-full flex justify-between items-center">
          <img alt="none" src={intLOGO} className="h-[45px]" />

          {/* Navbar */}
          <div className="hidden md:flex items-center">
            <ul className="flex gap-6 font-[600] text-sm">
              {navLink.map((link, index) => (
                <li key={index}>
                  <NavLink
                    to={link.path}
                    className={(navClass) =>
                      navClass.isActive
                        ? "text-greenColor"
                        : "hover:text-softColor"
                    }
                  >
                    {link.display}
                  </NavLink>
                </li>
              ))}
            </ul>
          </div>

          {/* Navbar Right Side */}
          <div className="hidden md:flex text-sm font-[700] text-greenColor">
            <p className="flex justify-between items-center px-6 gap-2">
              <i class="ri-user-5-line" /> Joaquin Burdado
            </p>
            <Link to="/">
              <button className="flex items-center px-4 py-1 gap-2 rounded-lg bg-transparent border hover:animate-pulse">
                <i class="ri-logout-circle-line" />
                Logout
              </button>
            </Link>
          </div>

          {/* Mobile Responsive View */}
          <div className="md:hidden cursor-pointer" onClick={handleClick}>
            {toggle ? (
              <i class="ri-close-line" />
            ) : (
              <i class="ri-menu-3-fill" />
            )}
          </div>
        </div>

        {/* Mobile Responsive view 2 */}
        <div
          className={
            toggle ? "absolute z-10 p-4 w-full px-8 md:hidden" : "hidden"
          }
        >
          <ul className="flex gap-6 font-[600] text-sm">
            {navLink.map((link, index) => (
              <li key={index}>
                <NavLink
                  to={link.path}
                  className={(navClass) =>
                    navClass.isActive
                      ? "text-greenColor"
                      : "hover:text-softColor"
                  }
                >
                  {link.display}
                </NavLink>
              </li>
            ))}

            <div className="flex flex-col my-4 gap-4 items-center justify-center text-greenColor">
              <p className="flex justify-between items-center px-6 gap-2">
                <i class="ri-user-5-line" /> Joaquin Burdado
              </p>
              <button className="flex items-center px-4 py-1 gap-2 rounded-lg bg-transparent border hover:animate-pulse">
                <i class="ri-logout-circle-line" />
                Logout
              </button>
            </div>
          </ul>
        </div>
      </div>
    </>
  );
};

export default Header;
